// Pega o modal
var modal = document.getElementById("modal-imagem");

// Pega o botão que abre o modal
var btn = document.getElementById("openImagem");

// Pega o elemento <span> que fecha o modal
var span = document.getElementsByClassName("close-imagem")[0];

// Verifica se o botão existe antes de adicionar o evento de clique
if (btn) {
    // Quando o usuário clicar no botão, abre o modal
    btn.onclick = function() {
        modal.style.display = "flex";
        const zoomist = new Zoomist("#zoomist", {
            slider: true,
            zoomer: true,
            draggable: true,
        });
    }
}

// Verifica se o elemento <span> existe antes de adicionar o evento de clique
if (span) {
    // Quando o usuário clicar no <span> (x), fecha o modal
    span.onclick = function() {
        modal.style.display = "none";
    }
}

// Quando o usuário clicar fora do modal, fecha o modal
window.onclick = function(event) {
    if (event.target == modal) {
        modal.style.display = "none";
    }
}
